import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { signin } from "../../actions/user"
import { Link } from 'react-router-dom'
const initailState = {
  email: "",
  password: "",
};
export default function Signin() {
  const [formData, setformData] = useState(initailState);
  const history = useNavigate();
  const dispatch = useDispatch();

  const onChange = (e) => {
    setformData({ ...formData, [e.target.name]: e.target.value });
  }

  const handleLogIn = (e) => {
    e.preventDefault();
    console.log(formData)
    dispatch(signin(formData, history));
  }


  return (
    <>
      <section className="my-5" >
      <div className="container" style={{maxWidth:"55%",marginLeft:"auto"}} >
    <div className="row d-flex justify-content-center align-items-center h-190">

        <div className="card text-black" style={{borderradius: "25px"}}>
          <div className="card-body p-md-5">
            <div className="row justify-content-center">
              <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">

                <p className="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">Log In</p>

                <form className="mx-1 mx-md-1">



                  <div className="d-flex flex-row align-items-center mb-4">
                    <i className="fas fa-envelope fa-lg me-3 fa-fw"></i>
                    <div className="form-outline flex-fill mb-4">
                      <label className="form-label" htmlFor="form3Example3c">Your Email</label>
                      <input type="email" id="email" name="email" onChange={onChange} className="form-control" />
                    </div>
                  </div>

                  <div className="d-flex flex-row align-items-center mb-4">
                    <i className="fas fa-lock fa-lg me-3 fa-fw"></i>
                    <div className="form-outline flex-fill mb-4">
                      <label className="form-label" htmlFor="form3Example4c">Password</label>
                      <input type="password" id="password" name="password" onChange={onChange} className="form-control" />
                    </div>
                  </div>
                  <div className="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                    <button type="button" onClick={handleLogIn} className="btn btn-outline-dark">Log In</button>
                  </div>
                  <div className="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                  <Link  to='/register' >
                    <h6 >New User ? </h6>
                    </Link>
                  </div>
                </form>

              </div>

            </div>
          </div>
        </div>

    </div>
  </div>
</section>


    </>
  );
}
